import { Fragment } from "react";
import { useParams, Route, Link } from "react-router-dom";
import HighlightedQuote from "../components/quotes/HighlightedQuote";
import Comments from "../components/comments/Comments";
const MY_QUTES = [
  { id: "q1", author: "hamad", text: "its a hard life" },
  { id: "q2", author: "mohammad", text: "its a hard life too" },
];

const QuteDetail = () => {
  const params = useParams();
  const qute = MY_QUTES.find((qute) => qute.id === params.quteid);
  if (!qute) {
    return <p>not Found</p>;
  }
  return (
    <Fragment>
      <HighlightedQuote
        text={qute.text}
        author={qute.author}
      ></HighlightedQuote>
      <Route path={`/qutes/${params.quteid}`} exact>
        <div className="centered">
          <Link to={`/qutes/${params.quteid}/comments`} className='btn--flat'>
            show comments
          </Link>
        </div>
      </Route>

      <Route path={`/qutes/${params.quteid}/comments`}>
        <Comments />
      </Route>
    </Fragment>
  );
};

export default QuteDetail;
